import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/AuthComponents.scss';

const PatientSignup = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    // Basic Info
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    phone: '',
    dateOfBirth: '',
    gender: '',
    address: '',
    city: '',
    state: '',
    zipCode: '',
    
    // Medical Info
    medicalCondition: '',
    diagnosisDate: '',
    currentMedications: '',
    allergies: '',
    previousSurgeries: '',
    fundingAmount: '',
    fundingPurpose: '',
    hospitalName: '',
    doctorName: '',
    treatmentStartDate: '',
    estimatedTreatmentDuration: '',
    
    // Documents
    documents: []
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      documents: [...prev.documents, ...files]
    }));
  };

  const removeDocument = (index) => {
    setFormData(prev => ({
      ...prev,
      documents: prev.documents.filter((_, i) => i !== index)
    }));
  };

  const validateStep = (step) => {
    const newErrors = {};
    
    switch (step) {
      case 1:
        if (!formData.firstName) newErrors.firstName = 'First name is required';
        if (!formData.lastName) newErrors.lastName = 'Last name is required';
        if (!formData.email) newErrors.email = 'Email is required';
        else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email is invalid';
        if (!formData.password) newErrors.password = 'Password is required';
        else if (formData.password.length < 8) newErrors.password = 'Password must be at least 8 characters';
        if (formData.password !== formData.confirmPassword) newErrors.confirmPassword = 'Passwords do not match';
        if (!formData.phone) newErrors.phone = 'Phone number is required';
        if (!formData.dateOfBirth) newErrors.dateOfBirth = 'Date of birth is required';
        if (!formData.gender) newErrors.gender = 'Gender is required';
        if (!formData.address) newErrors.address = 'Address is required';
        if (!formData.city) newErrors.city = 'City is required';
        if (!formData.state) newErrors.state = 'State is required';
        if (!formData.zipCode) newErrors.zipCode = 'ZIP code is required';
        break;
        
      case 2:
        if (!formData.medicalCondition) newErrors.medicalCondition = 'Medical condition is required';
        if (!formData.diagnosisDate) newErrors.diagnosisDate = 'Diagnosis date is required';
        if (!formData.fundingAmount) newErrors.fundingAmount = 'Funding amount is required';
        if (!formData.fundingPurpose) newErrors.fundingPurpose = 'Purpose of funding is required';
        if (!formData.hospitalName) newErrors.hospitalName = 'Hospital name is required';
        if (!formData.doctorName) newErrors.doctorName = 'Doctor name is required';
        if (!formData.treatmentStartDate) newErrors.treatmentStartDate = 'Treatment start date is required';
        if (!formData.estimatedTreatmentDuration) newErrors.estimatedTreatmentDuration = 'Treatment duration is required';
        break;
        
      case 3:
        if (formData.documents.length === 0) newErrors.documents = 'At least one document is required';
        break;
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validateStep(currentStep)) {
      setCurrentStep(prev => prev + 1);
    }
  };

  const handleBack = () => {
    setCurrentStep(prev => prev - 1);
  };

  const handleCompleteRegistration = async (e) => {
    e.preventDefault();
    
    if (!validateStep(currentStep)) {
      return;
    }

    try {
      // TODO: Implement actual registration logic with API call
      console.log('Registration data:', formData);
      
      // For now, just navigate to dashboard
      navigate('/dashboard');
    } catch (error) {
      console.error('Registration error:', error);
      setErrors(prev => ({
        ...prev,
        submit: 'Registration failed. Please try again.'
      }));
    }
  };

  const renderStepIndicator = () => (
    <div className="step-indicator">
      {[1, 2, 3].map(step => (
        <div
          key={step}
          className={`step ${currentStep === step ? 'active' : ''} ${currentStep > step ? 'completed' : ''}`}
        >
          <div className="step-number">{step}</div>
          <div className="step-label">
            {step === 1 ? 'Basic Info' : step === 2 ? 'Medical Info' : 'Documents'}
          </div>
        </div>
      ))}
    </div>
  );

  const renderBasicInfo = () => (
    <div className="form-step">
      <h2>Basic Information</h2>
      <div className="form-grid">
        <div className="form-group">
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            className={errors.firstName ? 'error' : ''}
          />
          {errors.firstName && <span className="error-message">{errors.firstName}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="lastName">Last Name</label>
          <input
            type="text"
            id="lastName"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            className={errors.lastName ? 'error' : ''}
          />
          {errors.lastName && <span className="error-message">{errors.lastName}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className={errors.email ? 'error' : ''}
          />
          {errors.email && <span className="error-message">{errors.email}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone Number</label>
          <input
            type="tel"
            id="phone"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            className={errors.phone ? 'error' : ''}
          />
          {errors.phone && <span className="error-message">{errors.phone}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="dateOfBirth">Date of Birth</label>
          <input
            type="date"
            id="dateOfBirth"
            name="dateOfBirth"
            value={formData.dateOfBirth}
            onChange={handleChange}
            className={errors.dateOfBirth ? 'error' : ''}
          />
          {errors.dateOfBirth && <span className="error-message">{errors.dateOfBirth}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="gender">Gender</label>
          <select
            id="gender"
            name="gender"
            value={formData.gender}
            onChange={handleChange}
            className={errors.gender ? 'error' : ''}
          >
            <option value="">Select Gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>
          {errors.gender && <span className="error-message">{errors.gender}</span>}
        </div>

        <div className="form-group full-width">
          <label htmlFor="address">Address</label>
          <input
            type="text"
            id="address"
            name="address"
            value={formData.address}
            onChange={handleChange}
            className={errors.address ? 'error' : ''}
          />
          {errors.address && <span className="error-message">{errors.address}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="city">City</label>
          <input
            type="text"
            id="city"
            name="city"
            value={formData.city}
            onChange={handleChange}
            className={errors.city ? 'error' : ''}
          />
          {errors.city && <span className="error-message">{errors.city}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="state">State</label>
          <input
            type="text"
            id="state"
            name="state"
            value={formData.state}
            onChange={handleChange}
            className={errors.state ? 'error' : ''}
          />
          {errors.state && <span className="error-message">{errors.state}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="zipCode">ZIP Code</label>
          <input
            type="text"
            id="zipCode"
            name="zipCode"
            value={formData.zipCode}
            onChange={handleChange}
            className={errors.zipCode ? 'error' : ''}
          />
          {errors.zipCode && <span className="error-message">{errors.zipCode}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className={errors.password ? 'error' : ''}
          />
          {errors.password && <span className="error-message">{errors.password}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="confirmPassword">Confirm Password</label>
          <input
            type="password"
            id="confirmPassword"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            className={errors.confirmPassword ? 'error' : ''}
          />
          {errors.confirmPassword && <span className="error-message">{errors.confirmPassword}</span>}
        </div>
      </div>
    </div>
  );

  const renderMedicalInfo = () => (
    <div className="form-step">
      <h2>Medical Information</h2>
      <div className="form-grid">
        <div className="form-group">
          <label htmlFor="medicalCondition">Medical Condition</label>
          <input
            type="text"
            id="medicalCondition"
            name="medicalCondition"
            value={formData.medicalCondition}
            onChange={handleChange}
            className={errors.medicalCondition ? 'error' : ''}
          />
          {errors.medicalCondition && <span className="error-message">{errors.medicalCondition}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="diagnosisDate">Diagnosis Date</label>
          <input
            type="date"
            id="diagnosisDate"
            name="diagnosisDate"
            value={formData.diagnosisDate}
            onChange={handleChange}
            className={errors.diagnosisDate ? 'error' : ''}
          />
          {errors.diagnosisDate && <span className="error-message">{errors.diagnosisDate}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="fundingAmount">Required Funding Amount ($)</label>
          <input
            type="number"
            id="fundingAmount"
            name="fundingAmount"
            value={formData.fundingAmount}
            onChange={handleChange}
            className={errors.fundingAmount ? 'error' : ''}
            min="0"
            step="0.01"
          />
          {errors.fundingAmount && <span className="error-message">{errors.fundingAmount}</span>}
        </div>

        <div className="form-group full-width">
          <label htmlFor="fundingPurpose">Purpose of Funding</label>
          <textarea
            id="fundingPurpose"
            name="fundingPurpose"
            value={formData.fundingPurpose}
            onChange={handleChange}
            className={errors.fundingPurpose ? 'error' : ''}
            rows="3"
            placeholder="Please describe why you need this funding and how it will be used..."
          />
          {errors.fundingPurpose && <span className="error-message">{errors.fundingPurpose}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="hospitalName">Hospital Name</label>
          <input
            type="text"
            id="hospitalName"
            name="hospitalName"
            value={formData.hospitalName}
            onChange={handleChange}
            className={errors.hospitalName ? 'error' : ''}
          />
          {errors.hospitalName && <span className="error-message">{errors.hospitalName}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="doctorName">Doctor's Name</label>
          <input
            type="text"
            id="doctorName"
            name="doctorName"
            value={formData.doctorName}
            onChange={handleChange}
            className={errors.doctorName ? 'error' : ''}
          />
          {errors.doctorName && <span className="error-message">{errors.doctorName}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="treatmentStartDate">Treatment Start Date</label>
          <input
            type="date"
            id="treatmentStartDate"
            name="treatmentStartDate"
            value={formData.treatmentStartDate}
            onChange={handleChange}
            className={errors.treatmentStartDate ? 'error' : ''}
          />
          {errors.treatmentStartDate && <span className="error-message">{errors.treatmentStartDate}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="estimatedTreatmentDuration">Estimated Treatment Duration (months)</label>
          <input
            type="number"
            id="estimatedTreatmentDuration"
            name="estimatedTreatmentDuration"
            value={formData.estimatedTreatmentDuration}
            onChange={handleChange}
            className={errors.estimatedTreatmentDuration ? 'error' : ''}
            min="1"
          />
          {errors.estimatedTreatmentDuration && <span className="error-message">{errors.estimatedTreatmentDuration}</span>}
        </div>

        <div className="form-group full-width">
          <label htmlFor="currentMedications">Current Medications</label>
          <textarea
            id="currentMedications"
            name="currentMedications"
            value={formData.currentMedications}
            onChange={handleChange}
            rows="3"
            placeholder="List any current medications..."
          />
        </div>

        <div className="form-group full-width">
          <label htmlFor="allergies">Allergies</label>
          <textarea
            id="allergies"
            name="allergies"
            value={formData.allergies}
            onChange={handleChange}
            rows="2"
            placeholder="List any allergies..."
          />
        </div>

        <div className="form-group full-width">
          <label htmlFor="previousSurgeries">Previous Surgeries</label>
          <textarea
            id="previousSurgeries"
            name="previousSurgeries"
            value={formData.previousSurgeries}
            onChange={handleChange}
            rows="2"
            placeholder="List any previous surgeries..."
          />
        </div>
      </div>
    </div>
  );

  const renderDocuments = () => (
    <div className="form-step">
      <h2>Upload Documents</h2>
      <div className="form-group">
        <label htmlFor="documents">Medical Documents</label>
        <div className="file-upload-container">
          <input
            type="file"
            id="documents"
            multiple
            onChange={handleFileChange}
            accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
            className={errors.documents ? 'error' : ''}
          />
          <p className="file-upload-hint">
            Upload medical reports, prescriptions, bills, or any relevant documents (PDF, DOC, JPG, PNG)
          </p>
          {errors.documents && <span className="error-message">{errors.documents}</span>}
        </div>

        {formData.documents.length > 0 && (
          <div className="uploaded-files">
            <h3>Uploaded Documents</h3>
            <ul>
              {formData.documents.map((file, index) => (
                <li key={index}>
                  <span>{file.name}</span>
                  <button
                    type="button"
                    onClick={() => removeDocument(index)}
                    className="remove-file"
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="auth-form-container">
      <form onSubmit={handleCompleteRegistration} className="auth-form">
        {renderStepIndicator()}
        
        {currentStep === 1 && renderBasicInfo()}
        {currentStep === 2 && renderMedicalInfo()}
        {currentStep === 3 && renderDocuments()}

        <div className="form-navigation">
          {currentStep > 1 && (
            <button type="button" onClick={handleBack} className="back-button">
              Back
            </button>
          )}
          
          {currentStep < 3 ? (
            <button type="button" onClick={handleNext} className="next-button">
              Next
            </button>
          ) : (
            <button type="submit" className="submit-button">
              Complete Registration
            </button>
          )}
        </div>
      </form>
    </div>
  );
};

export default PatientSignup;
