import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../styles/AuthComponents.scss';

const PatientSignup = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(0);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    condition: '',
    age: '',
    documents: []
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      documents: [...prev.documents, ...files]
    }));
  };

  const validateStep = (step) => {
    switch (step) {
      case 0:
        return formData.fullName && formData.email && formData.password && formData.confirmPassword;
      case 1:
        return formData.condition && formData.age;
      case 2:
        return true; // Documents are optional
      default:
        return false;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateStep(currentStep)) {
      alert('Please fill in all required fields');
      return;
    }

    if (currentStep < 2) {
      setCurrentStep(prev => prev + 1);
    } else {
      handleCompleteRegistration();
    }
  };

  const handleNext = () => {
    if (!validateStep(currentStep)) {
      alert('Please fill in all required fields');
      return;
    }
    setCurrentStep(prev => prev + 1);
  };

  const handleBack = () => {
    setCurrentStep(prev => prev - 1);
  };

  const handleCompleteRegistration = () => {
    // Validate all steps before final submission
    if (!validateStep(0) || !validateStep(1)) {
      alert('Please complete all required information');
      return;
    }

    // TODO: Add your API call here to register the patient
    console.log('Completing registration with data:', formData);
    
    // Navigate to dashboard after successful registration
    navigate('/dashboard');
  };

  const renderBasicInfo = () => (
    <div className={`form-section ${currentStep === 0 ? 'active' : ''}`}>
      <h3>Basic Information</h3>
      <div className="form-group">
        <label>Full Name</label>
        <input
          type="text"
          name="fullName"
          value={formData.fullName}
          onChange={handleInputChange}
          placeholder="Enter your full name"
          required
        />
      </div>
      <div className="form-group">
        <label>Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          placeholder="Enter your email"
          required
        />
      </div>
      <div className="form-group">
        <label>Password</label>
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleInputChange}
          placeholder="Create a password"
          required
        />
      </div>
      <div className="form-group">
        <label>Confirm Password</label>
        <input
          type="password"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleInputChange}
          placeholder="Confirm your password"
          required
        />
      </div>
    </div>
  );

  const renderMedicalInfo = () => (
    <div className={`form-section ${currentStep === 1 ? 'active' : ''}`}>
      <h3>Medical Information</h3>
      <div className="form-group">
        <label>Medical Condition</label>
        <textarea
          name="condition"
          value={formData.condition}
          onChange={handleInputChange}
          placeholder="Describe your medical condition in detail"
          required
        />
      </div>
      <div className="form-group">
        <label>Age</label>
        <input
          type="number"
          name="age"
          value={formData.age}
          onChange={handleInputChange}
          placeholder="Enter your age"
          min="0"
          max="120"
          required
        />
      </div>
    </div>
  );

  const renderDocuments = () => (
    <div className={`form-section ${currentStep === 2 ? 'active' : ''}`}>
      <h3>Medical Documents</h3>
      <div className="form-group">
        <label>Upload Documents</label>
        <div 
          className="file-upload"
          onClick={() => document.getElementById('fileInput').click()}
        >
          <input
            id="fileInput"
            type="file"
            multiple
            onChange={handleFileUpload}
            accept=".pdf,.jpg,.jpeg,.png"
            style={{ display: 'none' }}
          />
          <div className="upload-icon">üìÅ</div>
          <p className="file-hint">Drag & drop or click to upload doctor's notes, bills, prescriptions (PDF, JPG, PNG)</p>
        </div>
        {formData.documents.length > 0 && (
          <div className="uploaded-files">
            <h4>Uploaded Files:</h4>
            <ul>
              {formData.documents.map((file, index) => (
                <li key={index}>
                  <span>{file.name}</span>
                  <button
                    type="button"
                    onClick={() => {
                      setFormData(prev => ({
                        ...prev,
                        documents: prev.documents.filter((_, i) => i !== index)
                      }));
                    }}
                  >
                    ‚úï
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="auth-form-container patient-registration">
      <h2>Patient Registration</h2>
      <div className="progress-bar">
        <div className={`progress-step ${currentStep === 0 ? 'active' : currentStep > 0 ? 'completed' : ''}`}>
          1. Basic Info
        </div>
        <div className={`progress-step ${currentStep === 1 ? 'active' : currentStep > 1 ? 'completed' : ''}`}>
          2. Medical Info
        </div>
        <div className={`progress-step ${currentStep === 2 ? 'active' : ''}`}>
          3. Documents
        </div>
      </div>

      <form onSubmit={handleSubmit}>
        {renderBasicInfo()}
        {renderMedicalInfo()}
        {renderDocuments()}

        <div className="form-navigation">
          {currentStep > 0 && (
            <button
              type="button"
              onClick={handleBack}
              className="back-button"
            >
              ‚Üê Previous
            </button>
          )}
          <button 
            type="button" 
            onClick={currentStep === 2 ? handleCompleteRegistration : handleNext}
            className="submit-button"
          >
            {currentStep === 2 ? 'Complete Registration' : 'Next'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default PatientSignup;
