import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/AuthPage.scss';

const AuthPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [selectedRole, setSelectedRole] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);

  const roles = [
    {
      id: 'patient',
      title: 'Patient',
      description: 'Access healthcare funding and support',
      icon: 'üë§'
    },
    {
      id: 'donor',
      title: 'Donor',
      description: 'Contribute to healthcare initiatives',
      icon: 'üíù'
    },
    {
      id: 'hospital',
      title: 'Hospital',
      description: 'Manage healthcare resources and requests',
      icon: 'üè•'
    }
  ];

  const handleRoleSelect = (roleId) => {
    setSelectedRole(roleId);
    setCurrentSlide(1);
  };

  const handleBack = () => {
    setCurrentSlide(0);
    setSelectedRole(null);
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-header">
          <h1>{isLogin ? 'Welcome Back' : 'Create Account'}</h1>
          <p className="auth-subtitle">
            {isLogin ? 'Sign in to continue' : 'Join our healthcare platform'}
          </p>
        </div>

        <div className="auth-content">
          <div className={`slides-container ${currentSlide === 1 ? 'slide-left' : ''}`}>
            {/* Role Selection Slide */}
            <div className="slide role-selection">
              <h2>Select Your Role</h2>
              <div className="roles-grid">
                {roles.map((role) => (
                  <div
                    key={role.id}
                    className={`role-card ${selectedRole === role.id ? 'selected' : ''}`}
                    onClick={() => handleRoleSelect(role.id)}
                  >
                    <div className="role-icon">{role.icon}</div>
                    <h3>{role.title}</h3>
                    <p>{role.description}</p>
                  </div>
                ))}
              </div>
            </div>

            {/* Auth Form Slide */}
            <div className="slide auth-form">
              <button className="back-button" onClick={handleBack}>
                ‚Üê Back
              </button>
              <form onSubmit={(e) => e.preventDefault()}>
                {!isLogin && (
                  <div className="form-group">
                    <label>Full Name</label>
                    <input type="text" placeholder="Enter your full name" />
                  </div>
                )}
                <div className="form-group">
                  <label>Email</label>
                  <input type="email" placeholder="Enter your email" />
                </div>
                <div className="form-group">
                  <label>Password</label>
                  <input type="password" placeholder="Enter your password" />
                </div>
                {!isLogin && (
                  <div className="form-group">
                    <label>Confirm Password</label>
                    <input type="password" placeholder="Confirm your password" />
                  </div>
                )}
                <button type="submit" className="submit-button">
                  {isLogin ? 'Sign In' : 'Create Account'}
                </button>
              </form>
            </div>
          </div>

          <div className="auth-footer">
            <p>
              {isLogin ? "Don't have an account? " : 'Already have an account? '}
              <button
                className="toggle-auth"
                onClick={() => {
                  setIsLogin(!isLogin);
                  setCurrentSlide(0);
                  setSelectedRole(null);
                }}
              >
                {isLogin ? 'Sign Up' : 'Sign In'}
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
