import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const PatientSignup = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(0);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    condition: '',
    age: '',
    documents: []
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      documents: [...prev.documents, ...files]
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (currentStep < 2) {
      setCurrentStep(prev => prev + 1);
    } else {
      // TODO: Implement actual signup logic
      console.log('Patient signup:', formData);
      navigate('/dashboard');
    }
  };

  const renderBasicInfo = () => (
    <div className="form-section">
      <h3>Basic Information</h3>
      <div className="form-group">
        <label>Full Name</label>
        <input
          type="text"
          name="fullName"
          value={formData.fullName}
          onChange={handleInputChange}
          placeholder="Enter your full name"
          required
        />
      </div>
      <div className="form-group">
        <label>Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          placeholder="Enter your email"
          required
        />
      </div>
      <div className="form-group">
        <label>Password</label>
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleInputChange}
          placeholder="Create a password"
          required
        />
      </div>
      <div className="form-group">
        <label>Confirm Password</label>
        <input
          type="password"
          name="confirmPassword"
          value={formData.confirmPassword}
          onChange={handleInputChange}
          placeholder="Confirm your password"
          required
        />
      </div>
    </div>
  );

  const renderMedicalInfo = () => (
    <div className="form-section">
      <h3>Medical Information</h3>
      <div className="form-group">
        <label>Medical Condition</label>
        <input
          type="text"
          name="condition"
          value={formData.condition}
          onChange={handleInputChange}
          placeholder="Describe your medical condition"
          required
        />
      </div>
      <div className="form-group">
        <label>Age</label>
        <input
          type="number"
          name="age"
          value={formData.age}
          onChange={handleInputChange}
          placeholder="Enter your age"
          required
        />
      </div>
    </div>
  );

  const renderDocuments = () => (
    <div className="form-section">
      <h3>Medical Documents</h3>
      <div className="form-group">
        <label>Upload Documents</label>
        <div className="file-upload">
          <input
            type="file"
            multiple
            onChange={handleFileUpload}
            accept=".pdf,.jpg,.jpeg,.png"
          />
          <p className="file-hint">Drag & drop or click to upload doctor's notes, bills, prescriptions (PDF, JPG, PNG)</p>
        </div>
        {formData.documents.length > 0 && (
          <div className="uploaded-files">
            <h4>Uploaded Files:</h4>
            <ul>
              {formData.documents.map((file, index) => (
                <li key={index}>{file.name}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );

  return (
    <div className="auth-form-container">
      <h2>Patient Registration</h2>
      <div className="progress-bar">
        <div className={`progress-step ${currentStep === 0 ? 'active' : ''}`}>1. Basic Info</div>
        <div className={`progress-step ${currentStep === 1 ? 'active' : ''}`}>2. Medical Info</div>
        <div className={`progress-step ${currentStep === 2 ? 'active' : ''}`}>3. Documents</div>
      </div>

      <form onSubmit={handleSubmit}>
        {currentStep === 0 && renderBasicInfo()}
        {currentStep === 1 && renderMedicalInfo()}
        {currentStep === 2 && renderDocuments()}

        <div className="form-navigation">
          {currentStep > 0 && (
            <button
              type="button"
              onClick={() => setCurrentStep(prev => prev - 1)}
              className="back-button"
            >
              ← Previous
            </button>
          )}
          <button type="submit" className="submit-button">
            {currentStep === 2 ? 'Complete Registration' : 'Next'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default PatientSignup;
