import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/AuthPage.scss';

const AuthPage = () => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [selectedRole, setSelectedRole] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    password: '',
    confirmPassword: '',
    condition: '',
    age: '',
    documents: []
  });
  const [verificationSent, setVerificationSent] = useState(false);

  const roles = [
    {
      id: 'patient',
      title: 'Patient',
      description: 'Access healthcare funding and support',
      icon: 'üë§'
    },
    {
      id: 'donor',
      title: 'Donor',
      description: 'Contribute to healthcare initiatives',
      icon: 'üíù'
    },
    {
      id: 'hospital',
      title: 'Hospital',
      description: 'Manage healthcare resources and requests',
      icon: 'üè•'
    }
  ];

  const handleRoleSelect = (roleId) => {
    setSelectedRole(roleId);
    setCurrentSlide(1);
  };

  const handleBack = () => {
    setCurrentSlide(0);
    setSelectedRole(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      documents: [...prev.documents, ...files]
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (selectedRole === 'patient') {
      setVerificationSent(true);
      setCurrentSlide(2);
    }
  };

  const handleVerification = () => {
    // In a real app, this would verify the email
    navigate('/dashboard');
  };

  const renderPatientForm = () => (
    <form onSubmit={handleSubmit} className="patient-form">
      <div className="progress-bar">
        <div className="progress-step active">1. Basic Info</div>
        <div className="progress-step">2. Medical Info</div>
        <div className="progress-step">3. Documents</div>
      </div>

      <div className="form-section">
        <h3>Basic Information</h3>
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleInputChange}
            placeholder="Enter your full name"
            required
          />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            placeholder="Enter your email"
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            placeholder="Create a password"
            required
          />
        </div>
      </div>

      <div className="form-section">
        <h3>Medical Information</h3>
        <div className="form-group">
          <label>Medical Condition</label>
          <input
            type="text"
            name="condition"
            value={formData.condition}
            onChange={handleInputChange}
            placeholder="Describe your medical condition"
            required
          />
        </div>
        <div className="form-group">
          <label>Age</label>
          <input
            type="number"
            name="age"
            value={formData.age}
            onChange={handleInputChange}
            placeholder="Enter your age"
            required
          />
        </div>
      </div>

      <div className="form-section">
        <h3>Medical Documents</h3>
        <div className="form-group">
          <label>Upload Documents</label>
          <div className="file-upload">
            <input
              type="file"
              multiple
              onChange={handleFileUpload}
              accept=".pdf,.jpg,.jpeg,.png"
            />
            <p className="file-hint">Upload doctor's notes, bills, prescriptions (PDF, JPG, PNG)</p>
          </div>
          {formData.documents.length > 0 && (
            <div className="uploaded-files">
              <h4>Uploaded Files:</h4>
              <ul>
                {formData.documents.map((file, index) => (
                  <li key={index}>{file.name}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>

      <button type="submit" className="submit-button">
        Submit Application
      </button>
    </form>
  );

  const renderVerificationScreen = () => (
    <div className="verification-screen">
      <div className="verification-icon">‚úâÔ∏è</div>
      <h2>Verify Your Email</h2>
      <p>We've sent a verification link to your email address.</p>
      <p>Please check your inbox and click the link to verify your account.</p>
      <button onClick={handleVerification} className="verify-button">
        I've Verified My Email
      </button>
    </div>
  );

  const renderAuthForm = () => (
    <form onSubmit={handleSubmit}>
      {!isLogin && (
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            name="fullName"
            value={formData.fullName}
            onChange={handleInputChange}
            placeholder="Enter your full name"
          />
        </div>
      )}
      <div className="form-group">
        <label>Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          placeholder="Enter your email"
        />
      </div>
      <div className="form-group">
        <label>Password</label>
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleInputChange}
          placeholder="Enter your password"
        />
      </div>
      {!isLogin && (
        <div className="form-group">
          <label>Confirm Password</label>
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleInputChange}
            placeholder="Confirm your password"
          />
        </div>
      )}
      <button type="submit" className="submit-button">
        {isLogin ? 'Sign In' : 'Create Account'}
      </button>
    </form>
  );

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-header">
          <h1>{isLogin ? 'Welcome Back' : 'Create Account'}</h1>
          <p className="auth-subtitle">
            {isLogin ? 'Sign in to continue' : 'Join our healthcare platform'}
          </p>
        </div>

        <div className="auth-content">
          <div className={`slides-container ${currentSlide === 1 ? 'slide-left' : ''} ${currentSlide === 2 ? 'slide-left-2' : ''}`}>
            {/* Role Selection Slide */}
            <div className="slide role-selection">
              <h2>Select Your Role</h2>
              <div className="roles-grid">
                {roles.map((role) => (
                  <div
                    key={role.id}
                    className={`role-card ${selectedRole === role.id ? 'selected' : ''}`}
                    onClick={() => handleRoleSelect(role.id)}
                  >
                    <div className="role-icon">{role.icon}</div>
                    <h3>{role.title}</h3>
                    <p>{role.description}</p>
                  </div>
                ))}
              </div>
            </div>

            {/* Auth Form Slide */}
            <div className="slide auth-form">
              <button className="back-button" onClick={handleBack}>
                ‚Üê Back
              </button>
              {selectedRole === 'patient' ? renderPatientForm() : renderAuthForm()}
            </div>

            {/* Verification Slide */}
            {verificationSent && (
              <div className="slide verification">
                {renderVerificationScreen()}
              </div>
            )}
          </div>

          <div className="auth-footer">
            <p>
              {isLogin ? "Don't have an account? " : 'Already have an account? '}
              <button
                className="toggle-auth"
                onClick={() => {
                  setIsLogin(!isLogin);
                  setCurrentSlide(0);
                  setSelectedRole(null);
                }}
              >
                {isLogin ? 'Sign Up' : 'Sign In'}
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
